mapper.update:
UPDATE freeboard
    SET 
        bo_title   =  #{boTitle,jdbc=VARCHAR}
		,bo_writer  =  #{boWriter,jdbc=VARCHAR}
		,bo_ip      =  #{boIp,jdbc=VARCHAR} 
		,bo_email   =  #{boEmail,jdbc=VARCHAR}
		,bo_pass    =  #{boPass,jdbc=VARCHAR}
		,bo_content =  #{boContent,jdbc=COLB}
		,bo_date    =  sysdate
		,bo_rep     =  #{boRep,jdbc=INTEGER}
		,bo_hit     =  #{boHit,jdbc=INTEGER}
		,bo_rec     =  #{boRec,jdbc=INTEGER}
		,bo_parent  =  #{boParent,jdbc=INTEGER}
WHERE
    bo_no =#{boNo,jdbcType=INTEGER}

mapper.insert:
INSERT INTO freeboard (
    bo_no,    bo_title,    bo_writer,
    bo_ip,    bo_email,    bo_pass,
    bo_content,    bo_parent
) VALUES (
    #{boNo,jdbcType=INTEGER},    #{boTitle,jdbcType=VARCHAR},    #{boWriter,jdbcType=VARCHAR},
    #{boIp,jdbcType=VARCHAR},    #{boEmail,jdbcType=VARCHAR},    #{boPass,jdbcType=VARCHAR},
    #{boContent,jdbcType=CLOB},	    #{boParent,jdbcType=INTEGER}
)

mapper.select:
select c.*
from
	(select rownum rnum,b.*
	from (
		select a.*
		from freeboard a

		order by a.bo_date 
	)b
)c
where rnum between #{startRow} and #{endRow}
